<html>
  <style type="text/css">
  </style>
  <body>

    <script src="//plus.google.com/hangouts/_/api/v1/hangout.js"></script>

    <script>
      function embedVideo() {
        var embedUrl = document.getElementById("videoUrlInput").value;
        document.getElementById("videoIframe").setAttribute("src", embedUrl);
      }

      function init() {
        // When API is ready...
        gapi.hangout.onApiReady.add(
            function(eventObj) {
              if (eventObj.isApiReady) {
                document.getElementById('playButton')
                  .style.visibility = 'visible';
              }
            });
      }

      // Wait for gadget to load.
      gadgets.util.registerOnLoadHandler(init);
      </script>


    <h3>Video Watcher</h3>

    <p>
      <input type="text" id="videoUrlInput" />
      <input class="button" type="button" value="Play!" id="playButton"
	         style="visibility:hidden;"
	         onClick="embedVideo()"/>
      </p>

    <iframe id="videoIframe" />

    </body>
